package basics;

public class reinitializa {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num =10; // Declaring and  Initialization in single line 
		
		num = 20; // Reinitialization
		
		num =90; // Reinitialization
		
		
		int limit= 100;
		
		
		limit = num; // copying value from one variable to another variable.
		
		System.out.println(limit); 
		
		
		
	}

}
